data = open('dataset_3_2.txt')
pattern = data.read()
data.close()

def ReverseComplement(pattern):
    rev_pattern = ""
    for i in pattern[::-1]:
        temp = ""
        if i == "A":
            temp = "T"
        elif i == "T":
            temp = "A"
        elif i == "C":
            temp = "G"
        elif i == "G":
            temp = "C"
        rev_pattern = rev_pattern + temp
    return rev_pattern

rev = ReverseComplement(pattern)
print(rev)


    